import React, { useState } from 'react';
import './UserNavbar.css';
import logo from '../Assets/logo.png';
import { Link, useNavigate } from 'react-router-dom';
import { Bars3Icon } from '@heroicons/react/24/solid';
import {
    ShoppingBagIcon,
    UserCircleIcon,
    ArrowLeftEndOnRectangleIcon,
} from "@heroicons/react/24/outline";

const UserNavbar = () => {
  const [showLogoutModal, setShowLogoutModal] = useState(false);
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = (e) => {
    e.stopPropagation(); // Prevent immediate close
    setMenuOpen(prev => !prev);
  };
  

  const confirmLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  const cancelLogout = () => {
    setShowLogoutModal(false);
  };


  return (
    <section className='user-navbar'>
        <div className="user-login-menu">
            <Bars3Icon className="login-menu-btn" onClick={toggleMenu} />
        </div>
        <div className='user-nav-logo'>
            <img src={logo} alt="" className='user-img' />
        </div>
        <ul className='user-nav-menu'>
        <li>
            <Link className="user-nav-link" to="/">Rentals</Link>
        </li>
        <li>
            <Link className="user-nav-link" to="/">Post</Link>
        </li>
        <li>
            <Link className="user-nav-link" to="/about-us">About Us</Link>
        </li>
        <li>
            <Link className="user-nav-link" to="/contact">Contact</Link>
        </li>
        <div className='user-iconGroup'>
          <button className='user-iconButton' aria-label="Cart">
            <ShoppingBagIcon className='user-heroIcon'/>
          </button>
          
          <Link to="/profile">
            <button
              className='user-iconButton' aria-label="Profile">
              <UserCircleIcon className='user-heroIcon' />
            </button>
          </Link>

          <button className='user-iconButton' aria-label="Logout" onClick={() => setShowLogoutModal(true)}>
            <ArrowLeftEndOnRectangleIcon className='user-heroIcon' />
          </button>
        </div>
      </ul>

      {/* üîê Logout Modal */}
      {showLogoutModal && (
        <div className="user-modal-overlay">
          <div className="user-modal">
            <h3>Confirm Logout</h3>
            <p>Are you sure you want to log out?</p>
            <div className="user-modal-buttons">
              <button onClick={confirmLogout} className="user-confirm-btn">Logout</button>
              <button onClick={cancelLogout} className="user-cancel-btn">Cancel</button>
            </div>
          </div>
        </div>
      )}


    </section>
  );
};

export default UserNavbar;
