import './App.css';
import Navbar from './Components/Navbar/Navbar';
import Footer from './Components/Footer/Footer';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import RentNow from './Pages/RentNow';
import AboutPage from './Pages/AboutPage';
import ContactPage from './Pages/ContactPage';
import LoginForm from './Pages/LoginForm';
import RegisterForm from './Pages/RegisterForm';
import AdminForm from './Pages/AdminForm';
import RentalPage from './Pages/RentalPage';
import UserNavbar from './Components/Navbar/UserNavbar';

function App() {
  const location = useLocation();
  const hideNavbarAndFooterRoutes = ['/login', '/register', '/admin', '/dashboard', '/post'];
  const shouldHideNavbarAndFooter = hideNavbarAndFooterRoutes.includes(location.pathname);

  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsLoggedIn(!!token);
  }, [location]);

  return (
    <BrowserRouter>
      <Navbar/>

      <Routes>
        <Route path='/' element={<RentNow />} />
        <Route path='/about-us' element={<AboutPage />} />
        <Route path='/contact' element={<ContactPage />} />
        <Route path='/login' element={<LoginForm />} />
        <Route path='/register' element={<RegisterForm />} />
        <Route path='/admin' element={<AdminForm />} />
        <Route path='/rental-section' element={<RentalPage />} />
      </Routes>
      <Footer/>

    </BrowserRouter>
  );
}

export default App;
